        alumnos.add(lopez);
        alumnos.add(martinez);
        
        Materia lab = new Materia(12, "Laboratorio I", 1);
        materias.add(new Materia(10, "Inglés I", 1));
        materias.add(new Materia(11, "Matemáticas", 1));
        materias.add(lab);
        
        anadirMultiplesMaterias(lopez);
        anadirMultiplesMaterias(martinez);
        martinez.agregarMateria(lab);   
        
