    private static void anadirMultiplesMaterias(Alumno alumno) {
        for(Materia m : materias) {
            alumno.agregarMateria(m);
        }
    }
    
    public static void verAlumnosYMaterias() {
        System.out.println("-----------------------------------------");
        for(Alumno a : alumnos) {
            System.out.println(a.getNombre()+" "+a.getApellido());
            for (Materia m : a.getMaterias()) {
                System.out.println("---> "+m.getNombre());
            }
        }
    }

